const salesData = $input.first().json.body;
if (!Array.isArray(salesData)) {
  return [{ json: { productIndexes: [], error: "Sales data is not an array and cannot be processed."} }];
}
const productIndexes = salesData.map(sale => sale.index).filter(index => index !== undefined && index !== null);
if (productIndexes.length === 0 && salesData.length > 0) {
  return [{ json: { productIndexes: [], error: "No valid product indexes found in sales data."} }];
}
return [{ json: { productIndexes } }];

// Mengambil semua item output dari node "Get Product Data"
const productDataItems = $("Get Product Data1").all();

// Ekstrak objek .json dari setiap item dan simpan ke dalam array baru
// Ini adalah daftar produk aktual dari database yang akan kita gunakan untuk pencocokan
const productsInDatabase = productDataItems.map(item => item.json);
if (productsInDatabase.length > 0) {
  console.log("Produk Pertama dari Database (setelah .map):", JSON.stringify(productsInDatabase[0], null, 2));
} else {
  console.log("Tidak ada produk yang diambil dari database (productsInDatabase kosong setelah .map).");
}

// Data penjualan dari webhook
const salesData = $('Webhook').first().json.body;

let outputItems = []; // Array untuk menampung semua item yang akan di-output oleh node ini

// 1. Validasi Awal Data Penjualan (salesData)
if (!Array.isArray(salesData)) {
  outputItems.push({
    json: {
      type: "critical_error",
      errorType: "InvalidSalesDataFormat",
      message: "Data penjualan yang diterima bukan array.",
      originalSalesData: salesData
    }
  });
  return outputItems;
}

if (salesData.length === 0) {
  return [];
}

// 2. Proses Setiap Item Penjualan
salesData.forEach(sale => {
  // Pastikan setiap item penjualan (sale) memiliki 'index' produk
  if (sale.index === undefined || sale.index === null) {
    console.warn(`Item penjualan tidak memiliki 'index' yang valid: ${JSON.stringify(sale)}`);
    outputItems.push({
      json: {
        type: "product_error",
        error_details: "Item penjualan tidak memiliki 'index' yang valid.",
        sale_item: sale
      }
    });
    return; // Lanjut ke item penjualan berikutnya
  }

  // Konversi 'sale.index' dan 'product.index' ke tipe data yang konsisten (misalnya, String) untuk perbandingan
  // Ini penting untuk menghindari masalah ketidakcocokan tipe (angka vs string)
  const saleIndexToCompare = String(sale.index).trim(); // Hapus spasi berlebih jika ada

  // Cari produk yang sesuai di 'productsInDatabase' (yang sudah berisi objek .json)
  // Pastikan 'p.index' juga dikonversi ke String untuk perbandingan yang adil
  const productInDb = productsInDatabase.find(p => {
    if (p && (p.index !== undefined && p.index !== null)) {
      const dbIndexToCompare = String(p.index).trim();
      return dbIndexToCompare === saleIndexToCompare;
    }
    return false; // Jika p.index tidak valid, jangan cocokkan
  });

  if (productInDb) {
    const quantitySold = Number(sale.quantity_sold);
    const currentStock = Number(productInDb.stock);

    if (isNaN(quantitySold)) {
      console.warn(`Kuantitas terjual tidak valid untuk produk index "${saleIndexToCompare}": ${sale.quantity_sold}`);
      outputItems.push({
        json: {
          type: "data_error",
          product_name: productInDb.name || sale.name || null,
          product_index: productInDb.index,
          message: `Kuantitas terjual (quantity_sold) tidak valid: ${sale.quantity_sold}`
        }
      });
      return; 
    }
    if (isNaN(currentStock)) {
      console.warn(`Stok saat ini tidak valid untuk produk index "${saleIndexToCompare}" di database.`);
      outputItems.push({
        json: {
          type: "data_error",
          product_name: productInDb.name || sale.name || null,
          product_index: productInDb.index,
          message: `Stok produk di database tidak valid (NaN). Nilai stok: ${productInDb.stock}`
        }
      });
      return; 
    }

    const newStock = currentStock - quantitySold;
    outputItems.push({
      json: {
        index: productInDb.index,
        stock: Math.max(0, newStock)
      }
    });
  } else {
    console.warn(`Produk dengan index "${saleIndexToCompare}" TIDAK DITEMUKAN di database.`);
    outputItems.push({
      json: {
        type: "product_not_found",
        product_name: sale.name || null,
        product_index: sale.index, 
        quantity_sold: sale.quantity_sold,
        message: `Produk dengan index "${saleIndexToCompare}" (Nama: "${sale.name || 'Tidak ada nama'}") tidak ditemukan di database.`
      }
    });
  }
});

console.log("Output Akhir dari 'Process Sales & Identify Issues':", JSON.stringify(outputItems, null, 2));
return outputItems;

// 'items' adalah input ke node ini, yang berasal dari salah satu output node IF ("Route Data").
// Setiap 'item' dalam 'items' adalah objek yang menandakan suatu masalah.

// 1. Catat data yang diterima untuk debugging
console.log("AI Agent (Placeholder) - Menerima data untuk ditangani:");
items.forEach((item, index) => {
  console.log(`Item[${index}]: ${JSON.stringify(item.json, null, 2)}`);
});

// 2. Proses setiap item masalah dan buat pesan notifikasi
// Array untuk menampung hasil pemrosesan oleh AI placeholder ini
let processedAiItems = [];

items.forEach(item => {
  let notificationMessage = "Tidak ada tindakan spesifik yang diambil oleh AI placeholder untuk item ini.";
  let aiActionTaken = "Logged"; // Tindakan default

  // Ambil data JSON dari item
  const issueDetails = item.json;

  // Buat pesan notifikasi berdasarkan jenis masalah ('type')
  if (issueDetails.type === 'product_not_found') {
    notificationMessage = `PERHATIAN: Produk tidak ditemukan di database. 
    Nama Produk (dari sales): "${issueDetails.product_name || 'N/A'}", 
    Index Produk (dari sales): "${issueDetails.product_index}", 
    Kuantitas Terjual: ${issueDetails.quantity_sold}. 
    Saran: Verifikasi data produk atau tambahkan produk baru jika perlu.`;
    aiActionTaken = "Logged product_not_found, generated notification suggestion";

  } else if (issueDetails.type === 'critical_error') {
    notificationMessage = `ERROR KRITIS: ${issueDetails.message}. 
    Detail Data Asli: ${JSON.stringify(issueDetails.originalSalesData, null, 2)}. 
    Saran: Segera periksa sumber data penjualan atau format webhook.`;
    aiActionTaken = "Logged critical_error, generated notification suggestion";

  } else if (issueDetails.type === 'product_error') {
    notificationMessage = `ERROR DATA PRODUK (Penjualan): ${issueDetails.error_details}. 
    Detail Item Penjualan: ${JSON.stringify(issueDetails.sale_item, null, 2)}. 
    Saran: Periksa item penjualan yang masuk, pastikan memiliki semua field yang diperlukan (misalnya, 'index').`;
    aiActionTaken = "Logged product_error, generated notification suggestion";

  } else if (issueDetails.type === 'data_error') { // Menangani tipe error baru dari node sebelumnya
    notificationMessage = `ERROR VALIDASI DATA: ${issueDetails.message}. 
    Index Produk: "${issueDetails.product_index}", 
    Nama Produk: "${issueDetails.product_name || 'N/A'}".
    Saran: Periksa data terkait produk ini pada data penjualan atau database.`;
    aiActionTaken = "Logged data_error, generated notification suggestion";

  } else {
    notificationMessage = `Jenis masalah tidak dikenal diterima: ${JSON.stringify(issueDetails)}.`;
    aiActionTaken = "Logged unknown issue type";
  }

  // Untuk demonstrasi, kita akan membuat item baru yang berisi pesan notifikasi ini.
  // Dalam skenario nyata, Anda mungkin akan mengirim ini ke sistem notifikasi (Email, Slack, dll.)
  // atau melakukan pemanggilan API ke layanan AI.
  processedAiItems.push({
    json: {
      original_issue: issueDetails, // Menyimpan detail masalah asli
      ai_generated_notification: notificationMessage,
      ai_action_placeholder: aiActionTaken,
      status: "processed_by_ai_placeholder",
      timestamp_ai_processed: new Date().toISOString()
    }
  });
});

// 3. Kembalikan item yang telah diproses oleh AI placeholder
// Item-item ini bisa diteruskan ke node notifikasi atau node logging lainnya.
return processedAiItems;

